var skills = {
	data: data,
	getSkillById: function(id) {
		var skill = null;
		_.each(this.data, function(categorySkills, categoryName){
			if(skill)return;			
			skill = _.find(categorySkills, function(skill){
				return skill.skillId === id;
			});		
		});
		return skill;
	},
	getSkillIdByStyle: function(style){
		var map=  _.find(skillIdStyleMap, function(map){
			return style === map.style;
		});
		return (map && map.i) || null;
	},
	getSkillByStyle: function(style){
		var skillId = this.getSkillIdByStyle(style.substr(36));
		if(!skillId){
		  return null;
		}else{
		  return this.getSkillById(skillId);
		}
	}
};

module.exports = skills;